mapper.select:
select *
  from salon_blog
 order by no desc limit 1;

select comment_no as commentNo, no, content, reg_date as regDate, nick_name as nickName 
  from salon_blog_comment
 where no = #{no}
 order by comment_no;

select comment_no as commentNo, no, content, reg_date as regDate, nick_name as nickName 
  from salon_blog_comment
 where no = #{no}
 order by comment_no desc limit 1;
