mapper.select:
	select t1.no, t1.tag, t1.content, t2.no as pno, t2.file_no, t2.file_name 
      from salon_blog t1 left outer join salon_blog_image t2 on t1.no=t2.no
     order by t1.no;

	select t1.no, t1.tag, t1.content, t2.no as pno, t2.file_no, t2.file_name 
      from salon_blog t1 left outer join salon_blog_image t2 on t1.no=t2.no
	 where t1.no = #{no}

select file_name as fileName, no, file_no as fileNo 
  from salon_blog_image
 where no = #{no}

select comment_no as commentNo, no, content, reg_date as regDate, nick_name as nickName 
  from salon_blog_comment
 where no = #{no}
 order by comment_no;

select comment_no as commentNo, no, content, reg_date as regDate, nick_name as nickName 
from salon_blog_comment
where no = #{no}
order by comment_no desc limit 1;
