mapper.select:
	select t1.bno, t1.tag, t1.content, t2.bno, t2.bpno, t2.path 
      from blog t1 left outer join BLOG_PHO t2 on t1.bno=t2.bno
     where t1.mno = #{mno}
     order by t1.bno;

	select t1.bno, t1.tag, t1.content, t2.bno, t2.bpno, t2.path 
      from blog t1 left outer join BLOG_PHO t2 on t1.bno=t2.bno
	 where t1.bno = #{bno}

select path, bNo, bpno
  from BLOG_PHO
 where bno = #{bNo}

select cno, bno, content, reg_date as regDate, nick_name as nickName 
  from BLOG_COMMENT
 where bno = #{bno}
 order by cno;

select cno, bno, content, reg_date as regDate, nick_name as nickName 
from BLOG_COMMENT
where cno = #{cno}

    select mno, email, pwd, name, nick, photo_path as photoPath, gender, status
	  from member
	 where mno = #{mno}
